import React, { createContext, useState, useContext, useCallback } from 'react';

const NotificationContext = createContext();

export const useNotifications = () => {
  const context = useContext(NotificationContext);
  if (!context) {
    throw new Error('useNotifications must be used within NotificationProvider');
  }
  return context;
};

export const NotificationProvider = ({ children }) => {
  const [notifications, setNotifications] = useState([
    {
      id: '1',
      type: 'missing-class',
      title: 'Student Absent from Class',
      message: 'John Student is marked absent from Data Structures class',
      course: 'Data Structures',
      instructor: 'Dr. Smith',
      timestamp: new Date(Date.now() - 30 * 60000),
      read: false,
      studentName: 'John Student',
      attendanceId: 'CS2024001'
    },
    {
      id: '2',
      type: 'class-start',
      title: 'Class Starting Soon',
      message: 'Web Development class starts in 15 minutes',
      course: 'Web Development',
      instructor: 'Prof. Johnson',
      timestamp: new Date(Date.now() - 10 * 60000),
      read: false,
      classTime: '11:30 AM',
      location: 'Room B205'
    }
  ]);

  const [lecturerNotifications, setLecturerNotifications] = useState([
    {
      id: 'l1',
      type: 'missing-class',
      title: 'Sent to students',
      message: 'Notification about missing students sent',
      course: 'Data Structures',
      timestamp: new Date(Date.now() - 60 * 60000),
      status: 'sent',
      recipients: 15
    }
  ]);

  const addNotification = useCallback((notification) => {
    const newNotification = {
      id: Math.random().toString(36).substr(2, 9),
      timestamp: new Date(),
      read: false,
      ...notification
    };
    setNotifications(prev => [newNotification, ...prev]);
    return newNotification;
  }, []);

  const addLecturerNotification = useCallback((notification) => {
    const newNotification = {
      id: 'l' + Math.random().toString(36).substr(2, 9),
      timestamp: new Date(),
      status: 'sent',
      ...notification
    };
    setLecturerNotifications(prev => [newNotification, ...prev]);
    return newNotification;
  }, []);

  const markAsRead = useCallback((notificationId) => {
    setNotifications(prev =>
      prev.map(notif =>
        notif.id === notificationId ? { ...notif, read: true } : notif
      )
    );
  }, []);

  const deleteNotification = useCallback((notificationId) => {
    setNotifications(prev => prev.filter(notif => notif.id !== notificationId));
  }, []);

  const getUnreadCount = useCallback(() => {
    return notifications.filter(n => !n.read).length;
  }, [notifications]);

  const clearAllNotifications = useCallback(() => {
    setNotifications([]);
  }, []);

  return (
    <NotificationContext.Provider
      value={{
        notifications,
        lecturerNotifications,
        addNotification,
        addLecturerNotification,
        markAsRead,
        deleteNotification,
        getUnreadCount,
        clearAllNotifications
      }}
    >
      {children}
    </NotificationContext.Provider>
  );
};

export default NotificationContext;
