import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import './AdminDashboard.css';

function AdminDashboard() {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalStudents: 0,
    totalLecturers: 0,
    totalAdmins: 0,
    activeUsers: 0
  });
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTab, setSelectedTab] = useState('overview');

  useEffect(() => {
    fetchDemoUsers();
  }, []);

  const fetchDemoUsers = async () => {
    try {
      const response = await fetch('http://localhost:5000/auth/demo-users');
      const data = await response.json();

      if (data.success) {
        const userList = data.users || [];
        setUsers(userList);

        // Calculate stats
        const stats = {
          totalUsers: userList.length,
          totalStudents: userList.filter(u => u.role === 'student').length,
          totalLecturers: userList.filter(u => u.role === 'lecturer').length,
          totalAdmins: userList.filter(u => u.role === 'admin').length,
          activeUsers: userList.length
        };

        setStats(stats);
      }
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'student':
        return '#667eea';
      case 'lecturer':
        return '#764ba2';
      case 'admin':
        return '#ff6b6b';
      default:
        return '#999';
    }
  };

  const getRoleIcon = (role) => {
    switch (role) {
      case 'student':
        return 'üë®‚Äçüéì';
      case 'lecturer':
        return 'üë®‚Äçüè´';
      case 'admin':
        return 'üë®‚Äçüíº';
      default:
        return 'üë§';
    }
  };

  if (!user || user.role !== 'admin') {
    return (
      <div className="admin-container">
        <div className="admin-error">
          <h2>Access Denied</h2>
          <p>Only administrators can access this page.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-container">
      <div className="admin-header">
        <h1>Admin Dashboard</h1>
        <p>System Administration & Monitoring</p>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üë•</div>
          <div className="stat-content">
            <h3>Total Users</h3>
            <p className="stat-value">{stats.totalUsers}</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üë®‚Äçüéì</div>
          <div className="stat-content">
            <h3>Students</h3>
            <p className="stat-value">{stats.totalStudents}</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üë®‚Äçüè´</div>
          <div className="stat-content">
            <h3>Lecturers</h3>
            <p className="stat-value">{stats.totalLecturers}</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üë®‚Äçüíº</div>
          <div className="stat-content">
            <h3>Admins</h3>
            <p className="stat-value">{stats.totalAdmins}</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üü¢</div>
          <div className="stat-content">
            <h3>Active Users</h3>
            <p className="stat-value">{stats.activeUsers}</p>
          </div>
        </div>
      </div>

      {/* Tabs */}
      <div className="admin-tabs">
        <button
          className={`tab-button ${selectedTab === 'overview' ? 'active' : ''}`}
          onClick={() => setSelectedTab('overview')}
        >
          üìä Overview
        </button>
        <button
          className={`tab-button ${selectedTab === 'users' ? 'active' : ''}`}
          onClick={() => setSelectedTab('users')}
        >
          üë• Users
        </button>
        <button
          className={`tab-button ${selectedTab === 'settings' ? 'active' : ''}`}
          onClick={() => setSelectedTab('settings')}
        >
          ‚öôÔ∏è Settings
        </button>
      </div>

      {/* Tab Content */}
      <div className="admin-content">
        {selectedTab === 'overview' && (
          <div className="overview-section">
            <h2>System Overview</h2>
            <div className="overview-grid">
              <div className="overview-card">
                <h3>System Status</h3>
                <p className="status-indicator">üü¢ Online</p>
                <p className="status-text">All systems operational</p>
              </div>

              <div className="overview-card">
                <h3>Database</h3>
                <p className="status-indicator">üü¢ Connected</p>
                <p className="status-text">In-memory database (Demo)</p>
              </div>

              <div className="overview-card">
                <h3>WebSocket</h3>
                <p className="status-indicator">üü¢ Active</p>
                <p className="status-text">Real-time communication</p>
              </div>

              <div className="overview-card">
                <h3>API Health</h3>
                <p className="status-indicator">üü¢ Healthy</p>
                <p className="status-text">All endpoints responsive</p>
              </div>
            </div>

            <div className="overview-features">
              <h3>Enabled Features</h3>
              <ul className="features-list">
                <li>‚úÖ Multi-role Authentication (Student, Lecturer, Admin)</li>
                <li>‚úÖ Real-time Notifications via WebSocket</li>
                <li>‚úÖ JWT Token-based Sessions</li>
                <li>‚úÖ Role-based Access Control</li>
                <li>‚úÖ QR Code Attendance Tracking</li>
                <li>‚úÖ AI-powered Analytics</li>
                <li>‚úÖ Push Notifications</li>
                <li>‚úÖ Admin Dashboard & Monitoring</li>
              </ul>
            </div>
          </div>
        )}

        {selectedTab === 'users' && (
          <div className="users-section">
            <h2>User Management</h2>
            {loading ? (
              <p className="loading">Loading users...</p>
            ) : (
              <div className="users-list">
                {users.map((userData, index) => (
                  <div key={index} className="user-card">
                    <div className="user-avatar">{getRoleIcon(userData.role)}</div>
                    <div className="user-info">
                      <h4>{userData.name}</h4>
                      <p className="user-email">{userData.email}</p>
                      <p className="user-role">{userData.role.toUpperCase()}</p>
                    </div>
                    <div className="user-actions">
                      <button className="btn-action">Edit</button>
                      <button className="btn-action danger">Delete</button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {selectedTab === 'settings' && (
          <div className="settings-section">
            <h2>System Settings</h2>
            <div className="settings-form">
              <div className="setting-item">
                <label>System Name</label>
                <input type="text" value="ClassTrack AI" readOnly />
              </div>

              <div className="setting-item">
                <label>Database Type</label>
                <input type="text" value="In-Memory (Demo)" readOnly />
              </div>

              <div className="setting-item">
                <label>Backend Port</label>
                <input type="text" value="5000" readOnly />
              </div>

              <div className="setting-item">
                <label>Frontend Port</label>
                <input type="text" value="3000" readOnly />
              </div>

              <div className="setting-item">
                <label>JWT Expiry</label>
                <input type="text" value="24 hours" readOnly />
              </div>

              <div className="setting-item">
                <label>WebSocket Status</label>
                <div className="status-badge">üü¢ Active</div>
              </div>

              <button className="btn-primary">Save Settings</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
