import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Auth.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    // Basic validation
    if (!email || !password) {
      setError('Please fill in all fields');
      setIsLoading(false);
      return;
    }

    if (!email.includes('@')) {
      setError('Please enter a valid email');
      setIsLoading(false);
      return;
    }

    // Simulate API call
    setTimeout(() => {
      try {
        // Demo credentials
        if (email.toLowerCase() === 'student@university.edu' && password === 'password123') {
          login({
            id: '1',
            name: 'John Student',
            email: email,
            role: 'student',
            avatar: 'üë®‚Äçüéì',
            studentId: 'CS2024001',
            department: 'Computer Science'
          });
          navigate('/');
        } else if (email.toLowerCase() === 'lecturer@university.edu' && password === 'password123') {
          login({
            id: '2',
            name: 'Dr. Smith',
            email: email,
            role: 'lecturer',
            avatar: 'üë®‚Äçüè´',
            studentId: 'PROF001',
            department: 'Computer Science'
          });
          navigate('/');
        } else {
          setError('Invalid email or password. Try student@university.edu or lecturer@university.edu with password123');
        }
      } catch (err) {
        setError('Login failed. Please try again.');
      } finally {
        setIsLoading(false);
      }
    }, 500);
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <div className="auth-header">
          <div className="auth-logo">üìö</div>
          <h1>ClassTrack AI</h1>
          <p>Attendance & Learning Management System</p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          <h2>Welcome Back!</h2>

          {error && <div className="error-alert">{error}</div>}

          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              disabled={isLoading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              disabled={isLoading}
            />
          </div>

          <div className="form-remember">
            <label>
              <input type="checkbox" />
              <span>Remember me</span>
            </label>
            <Link to="/forgot-password" className="forgot-link">
              Forgot password?
            </Link>
          </div>

          <button type="submit" className="btn-primary" disabled={isLoading}>
            {isLoading ? 'Signing in...' : 'Sign In'}
          </button>
        </form>

        <div className="auth-divider">
          <span>OR</span>
        </div>

        <div className="auth-footer">
          <p>Don't have an account? <Link to="/signup" className="auth-link">Sign up here</Link></p>
        </div>

        <div className="demo-credentials">
          <h4>üìù Demo Credentials:</h4>
          <p><strong>Student:</strong></p>
          <p>Email: student@university.edu</p>
          <p>Password: password123</p>
          <hr />
          <p><strong>Lecturer:</strong></p>
          <p>Email: lecturer@university.edu</p>
          <p>Password: password123</p>
        </div>
      </div>

      <div className="auth-background">
        <div className="floating-icon">üìö</div>
        <div className="floating-icon">üìä</div>
        <div className="floating-icon">‚úÖ</div>
        <div className="floating-icon">üí¨</div>
      </div>
    </div>
  );
}

export default Login;
